// LoginRegisterPage.js
import React, { useState } from 'react';
import LoginForm from './LoginForm'; // Create a separate component for the login form
import RegisterForm from './RegisterForm'; // Create a separate component for the register form

const LoginRegisterPage = () => {
  const [loginMode, setLoginMode] = useState(true);

  const handleToggleForm = () => {
    setLoginMode(!loginMode);
  };

  return (
    <div className="page-container">
      <div className="toggle-button-container">
        <button onClick={() => setLoginMode(true)}>Login</button>
        <button onClick={() => setLoginMode(false)}>Register</button>
      </div>

      {loginMode ? <LoginForm /> : <RegisterForm />}

      <div className="toggle-form" onClick={handleToggleForm}>
        {loginMode ? "Don't have an account? Register here." : 'Already have an account? Login here.'}
      </div>
    </div>
  );
};

export default LoginRegisterPage;
