import React, { useState } from 'react';
import SplitMoney from '../../splittingMoney/SplitMoney';



const Register = ({ handleRegister }) => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [repassword, setRepassword] = useState('');
  
    const handleSubmit = (e) => {
      e.preventDefault(); // Prevent default form submission behavior
      handleRegister(username, password, repassword);
    };
  
    return (
      <div>
        <h2>Register Page</h2>
        <form onSubmit={handleSubmit}>
          <label>
            Enter the Username:
            <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} />
          </label>
          <br />
          <br />
          <label>
            Enter the Password:
            <input type="text" value={password} required onChange={(e) => setPassword(e.target.value)} />
          </label>
          <br />
          <br />
          <label>
            ReEnter the Password:
            <input type="text" value={repassword} required onChange={(e) => setRepassword(e.target.value)} />
          </label>
          <br />
          <br />
          <button type="submit">Register</button>
        </form>
      </div>
    );
  };
  

const RegisterPage = () => {
  
    const [onClickRegister, setOnClickRegister] = useState(false);

  const handleRegister = async (username,password,repassword) => {
    if(password === repassword && password !=='')
    {
        console.log(username);
        try {
            const response = await fetch('http://localhost:8080/register', {
              method: 'POST',
              headers: {
                'Content-Type': 'application/json',
              },
              body: JSON.stringify({ 'username':username, 'password':password }),
            });
      
            if (!response.ok) {
              throw new Error('Error during registration');
            }
      
            const data = await response.json();
            console.log('User registered successfully:', data);
          } catch (error) {
            console.error('Error during registration:', error);
          }
        setOnClickRegister(true);
    }
    else{
        alert("Wrong Credentials");
    }
   
  };

  return (
    <div>
        {
            onClickRegister?<SplitMoney></SplitMoney>:<Register handleRegister={handleRegister}/>
        }
    </div>

  );
};

export default RegisterPage;
