// LoginRegisterForm.js
import React, { useState } from 'react';
import '../../Css/LoginRegisterForm.css'

const LoginRegisterForm = () => {
  const [loginMode, setLoginMode] = useState(true);
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleToggleForm = () => {
    setLoginMode(!loginMode);
  };

  const handleLogin = () => {
    // Implement login logic here
    console.log('Login clicked');
  };

  const handleRegister = () => {
    // Implement registration logic here
    console.log('Register clicked');
  };

  return (
    <div className="form-container">
      <form className="form">
        {loginMode ? <h2>Login</h2> : <h2>Register</h2>}

        {!loginMode && (
          <label>
            Name:
            <input type="text" value={name} onChange={(e) => setName(e.target.value)} />
          </label>
        )}

        <label>
          Email:
          <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
        </label>

        <label>
          Password:
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
        </label>

        <div className="button-container">
          {loginMode ? (
            <button type="button" onClick={handleLogin}>
              Login
            </button>
          ) : (
            <button type="button" onClick={handleRegister}>
              Register
            </button>
          )}
        </div>

        <div className="toggle-form" onClick={handleToggleForm}>
          {loginMode ? "Don't have an account? Register here." : 'Already have an account? Login here.'}
        </div>
      </form>
    </div>
  );
};

export default LoginRegisterForm;
